import React, {useEffect} from 'react'
import { connect } from 'react-redux'
import {AuctionCard} from './AuctionCard'
import {fetchingAuction} from '../actions'
//styling imports
import { makeStyles, useTheme } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Typography from "@material-ui/core/Typography";



const useStyles = makeStyles(theme => ({
    root: {
      display: "flex"
    },
    details: {
      display: "flex",
      flexDirection: "column"
    },
    cover: {
      width: 151,
      background: "red"
    }
  }));



function AuctionList(props) {
    console.log(`AuctionList`,props)
    useEffect(()=>{
        props.fetchingAuction()
    }, [])
    const classes = useStyles();
    const theme = useTheme();
    return (
        <div>
            {props.auctions.map((auction)=>{
                console.log("auction",auction)
                // return <AuctionCard key={auction.id} auction={auction}/>
                return (<Card className={classes.root}>
                    <CardMedia
                        className={classes.cover}
                        image="https://images.pexels.com/photos/3713709/pexels-photo-3713709.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260"
                        title="Live from space album cover"
                    />
                    <div className={classes.details}>
                        <CardContent className={classes.content}>
                            <Typography component="h5" variant="h5">
                            {auction.name} - by {auction.seller}
                            </Typography>
                            <Typography variant="subtitle1" color="textPrimary">
                            {auction.starting_price}
                            </Typography>
                            <Typography variant="body1" color="textSecondary">
                            Closes {auction.deadline}
                            </Typography>
                        </CardContent>
                    </div>
                    </Card>)



            })}
            
        </div>
    )
}

export default connect(state=> {
    return{
        auctions: state.crudReducer.auctions
    }
}, {fetchingAuction})(AuctionList)
