import React from 'react'
import { useHistory,useParams } from 'react-router-dom'
//styling imports
import { makeStyles, useTheme } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Typography from "@material-ui/core/Typography";

const useStyles = makeStyles(theme => ({
    root: {
      display: "flex"
    },
    details: {
      display: "flex",
      flexDirection: "column"
    },
    cover: {
      width: 151,
      background: "red"
    }
  }));


export const AuctionCard = (props) => {

    let history = useHistory()

    const sendToCloserLook = (id, auctionData) => {
        history.push(`/closerlook/${id}`, auctionData)
    }

    const classes = useStyles();
    const theme = useTheme();

    return (
        // <div>
        //     <p onClick={() => {sendToCloserLook(props.auction.id, props.auction)}}>{props.auction.name}</p>
        // </div>
        <Card className={classes.root} onClick={() => {sendToCloserLook(props.auction.id, props.auction)}}>
            <CardMedia
                className={classes.cover}
                image="https://images.pexels.com/photos/3713709/pexels-photo-3713709.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260"
                title="Live from space album cover"
            />
            <div className={classes.details}>
                <CardContent className={classes.content}>
                    <Typography component="h5" variant="h5">
                        {props.auction.name} - by {props.auction.seller}
                    </Typography>
                    <Typography variant="subtitle1" color="textPrimary">
                        {props.auction.starting_price}
                    </Typography>
                    <Typography variant="body1" color="textSecondary">
                        Closes {props.auction.deadline}
                    </Typography>
                </CardContent>
            </div>
        </Card>
    )
}
